/**
 * Formats template string with appropriate value corresponding to given variable placeholder
 *
 * @param {string} string - template string to be formatted
 * @param {string} variableName - variable (placeholder) name to be fulfilled in given string
 * @param {any} value - value to be inserted
 *
 * @returns {string} - formatted string
 */
export function formatTemplatedString(string: string, variableName: string, value: any): string;
